import { useState } from "react";

const initialGoods = [
  { name: "교복 떡메 (남자 하복)", price: 1500 },
  { name: "교복 떡메 (여자 하복)", price: 1500 },
  { name: "교복 떡메 (1학년 생활복)", price: 1500 },
  { name: "교복 떡메 (2학년 생활복)", price: 1500 },
  { name: "교복 떡메 (3학년 생활복)", price: 1500 },
  { name: "학교 전경 엽서", price: 1500 },
  { name: "학교 건물 스티커", price: 1000 },
  { name: "띠부띠부씰 (1)", price: 500 },
  { name: "띠부띠부씰 (2)", price: 500 },
  { name: "띠부띠부씰 (3)", price: 500 },
  { name: "띠부띠부씰 (4)", price: 500 },
  { name: "학교 건물 포스트잇 (1)", price: 1000 },
  { name: "학교 건물 포스트잇 (2)", price: 1000 },
];

export default function GoodsTracker() {
  const [goods, setGoods] = useState(
    initialGoods.map((item) => ({ ...item, sold: 0 }))
  );

  const handleAdjust = (index, delta) => {
    setGoods((prevGoods) =>
      prevGoods.map((item, i) =>
        i === index ? { ...item, sold: Math.max(0, item.sold + delta) } : item
      )
    );
  };

  const totalRevenue = goods.reduce((sum, item) => sum + item.price * item.sold, 0);

  return (
    <div className="bg-pink-100 min-h-screen flex flex-col items-center justify-center p-6">
      <h1 className="text-4xl font-bold text-pink-600 mb-4">🌸 학홍 굿즈 판매 현황 🌸</h1>
      <div className="bg-white p-6 rounded-2xl shadow-lg w-full max-w-3xl">
        <table className="w-full border-collapse">
          <thead>
            <tr className="bg-pink-300 text-white">
              <th className="p-2">상품명</th>
              <th className="p-2">가격</th>
              <th className="p-2">판매 수량</th>
              <th className="p-2">조정</th>
            </tr>
          </thead>
          <tbody>
            {goods.map((item, index) => (
              <tr key={index} className="border-b">
                <td className="p-2 text-center">{item.name}</td>
                <td className="p-2 text-center">{item.price}원</td>
                <td className="p-2 text-center">{item.sold}</td>
                <td className="p-2 text-center">
                  <button
                    onClick={() => handleAdjust(index, -1)}
                    className="bg-pink-500 text-white px-3 py-1 rounded-full mx-1"
                  >
                    -
                  </button>
                  <button
                    onClick={() => handleAdjust(index, 1)}
                    className="bg-pink-500 text-white px-3 py-1 rounded-full mx-1"
                  >
                    +
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
      <div className="mt-4 text-2xl font-bold text-pink-700">
        총 판매 금액: {totalRevenue.toLocaleString()}원
      </div>
    </div>
  );
}
