import { useState, useEffect } from "react";

// ÏÉÅÌíà Ïπ¥ÌÖåÍ≥†Î¶¨ Î∞è Ìï¥Îãπ ÏÉÅÌíà Î™©Î°ù
const categories = [
  { name: "ÍµêÎ≥µ Îñ°Î©î", items: [
    { name: "ÎÇ®Ïûê ÌïòÎ≥µ", price: 1500 },
    { name: "Ïó¨Ïûê ÌïòÎ≥µ", price: 1500 },
    { name: "1ÌïôÎÖÑ ÏÉùÌôúÎ≥µ", price: 1500 },
    { name: "2ÌïôÎÖÑ ÏÉùÌôúÎ≥µ", price: 1500 },
    { name: "3ÌïôÎÖÑ ÏÉùÌôúÎ≥µ", price: 1500 }
  ]},
  { name: "ÌïôÍµê Ï†ÑÍ≤Ω ÏóΩÏÑú", items: [
    { name: "ÌïôÍµê Ï†ÑÍ≤Ω ÏóΩÏÑú", price: 1500 }
  ]},
  { name: "ÌïôÍµê Í±¥Î¨º Ïä§Ìã∞Ïª§", items: [
    { name: "ÌïôÍµê Í±¥Î¨º Ïä§Ìã∞Ïª§", price: 1000 }
  ]},
  { name: "Îù†Î∂ÄÎù†Î∂ÄÏî∞", items: [
    { name: "1", price: 500 },
    { name: "2", price: 500 },
    { name: "3", price: 500 },
    { name: "4", price: 500 }
  ]},
  { name: "ÌïôÍµê Í±¥Î¨º Ìè¨Ïä§Ìä∏Ïûá", items: [
    { name: "1", price: 1000 },
    { name: "2", price: 1000 }
  ]}
];

export default function GoodsTracker() {
  const [goods, setGoods] = useState(() => {
    const saved = localStorage.getItem("hakHongGoods");
    return saved
      ? JSON.parse(saved)
      : categories.map((category) => ({
          name: category.name,
          items: category.items.map((item) => ({ ...item, sold: 0 })),
        }));
  });

  useEffect(() => {
    localStorage.setItem("hakHongGoods", JSON.stringify(goods));
  }, [goods]);

  const handleAdjust = (categoryIndex, itemIndex, delta) => {
    setGoods((prevGoods) =>
      prevGoods.map((category, cIndex) =>
        cIndex === categoryIndex
          ? {
              ...category,
              items: category.items.map((item, iIndex) =>
                iIndex === itemIndex ? { ...item, sold: Math.max(0, item.sold + delta) } : item
              )
            }
          : category
      )
    );
  };

  const totalRevenue = goods.reduce(
    (sum, category) =>
      sum + category.items.reduce((catSum, item) => catSum + item.price * item.sold, 0),
    0
  );

  // Ï¢åÏö∞ Î∂ÑÌï†
  const leftCategories = goods.slice(0, Math.ceil(goods.length / 2));
  const rightCategories = goods.slice(Math.ceil(goods.length / 2));

  return (
    <div className="fixed inset-0 bg-pink-100 flex flex-col items-center p-8 overflow-y-auto">
      <h1 className="text-4xl font-bold text-pink-600 mb-8">üå∏ ÌïôÌôç ÍµøÏ¶à ÌåêÎß§ ÌòÑÌô© üå∏</h1>

      <div className="flex w-full justify-between px-12 gap-8">
        {/* ÏôºÏ™Ω */}
        <div className="w-1/2 flex flex-col space-y-6">
          {leftCategories.map((category, categoryIndex) => (
            <div key={categoryIndex} className="bg-white p-6 rounded-3xl shadow-lg border border-gray-300 w-full">
              <h2 className="text-xl font-semibold mb-4 text-pink-600">{category.name}</h2>
              <table className="w-full">
                <thead>
                  <tr className="bg-pink-300 text-white text-lg">
                    <th className="p-2">ÏÉÅÌíàÎ™Ö</th>
                    <th className="p-2">Í∞ÄÍ≤©</th>
                    <th className="p-2">ÏàòÎüâ</th>
                    <th className="p-2">Ï°∞Ï†ï</th>
                  </tr>
                </thead>
                <tbody>
                  {category.items.map((item, itemIndex) => (
                    <tr key={itemIndex} className="text-center border-t">
                      <td className="p-2">{item.name}</td>
                      <td className="p-2">{item.price}Ïõê</td>
                      <td className="p-2">{item.sold}</td>
                      <td className="p-2">
                        <button
                          onClick={() => handleAdjust(categoryIndex, itemIndex, -1)}
                          className="bg-pink-500 text-white px-4 py-1 rounded-full mx-1"
                        >
                          -
                        </button>
                        <button
                          onClick={() => handleAdjust(categoryIndex, itemIndex, 1)}
                          className="bg-pink-500 text-white px-4 py-1 rounded-full mx-1"
                        >
                          +
                        </button>
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          ))}
        </div>

        {/* Ïò§Î•∏Ï™Ω */}
        <div className="w-1/2 flex flex-col space-y-6">
          {rightCategories.map((category, categoryIndex) => (
            <div key={categoryIndex} className="bg-white p-6 rounded-3xl shadow-lg border border-gray-300 w-full">
              <h2 className="text-xl font-semibold mb-4 text-pink-600">{category.name}</h2>
              <table className="w-full">
                <thead>
                  <tr className="bg-pink-300 text-white text-lg">
                    <th className="p-2">ÏÉÅÌíàÎ™Ö</th>
                    <th className="p-2">Í∞ÄÍ≤©</th>
                    <th className="p-2">ÏàòÎüâ</th>
                    <th className="p-2">Ï°∞Ï†ï</th>
                  </tr>
                </thead>
                <tbody>
                  {category.items.map((item, itemIndex) => (
                    <tr key={itemIndex} className="text-center border-t">
                      <td className="p-2">{item.name}</td>
                      <td className="p-2">{item.price}Ïõê</td>
                      <td className="p-2">{item.sold}</td>
                      <td className="p-2">
                        <button
                          onClick={() => handleAdjust(categoryIndex + leftCategories.length, itemIndex, -1)}
                          className="bg-pink-500 text-white px-4 py-1 rounded-full mx-1"
                        >
                          -
                        </button>
                        <button
                          onClick={() => handleAdjust(categoryIndex + leftCategories.length, itemIndex, 1)}
                          className="bg-pink-500 text-white px-4 py-1 rounded-full mx-1"
                        >
                          +
                        </button>
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          ))}
        </div>
      </div>

      {/* Ï¥ùÌï© */}
      <div className="mt-10 text-3xl font-bold text-pink-700">
        Ï¥ù ÌåêÎß§ Í∏àÏï°: {totalRevenue.toLocaleString()}Ïõê
      </div>
    </div>
  );
}
