import { useState, useEffect } from "react";

const categories = [
  {
    name: "ÍµêÎ≥µ Îñ°Î©î",
    items: [
      { name: "ÎÇ®Ïûê ÌïòÎ≥µ", price: 1500 },
      { name: "Ïó¨Ïûê ÌïòÎ≥µ", price: 1500 },
      { name: "1ÌïôÎÖÑ ÏÉùÌôúÎ≥µ", price: 1500 },
      { name: "2ÌïôÎÖÑ ÏÉùÌôúÎ≥µ", price: 1500 },
      { name: "3ÌïôÎÖÑ ÏÉùÌôúÎ≥µ", price: 1500 }
    ]
  },
  {
    name: "ÌïôÍµê Ï†ÑÍ≤Ω ÏóΩÏÑú",
    items: [{ name: "ÌïôÍµê Ï†ÑÍ≤Ω ÏóΩÏÑú", price: 1500 }]
  },
  {
    name: "ÌïôÍµê Í±¥Î¨º Ïä§Ìã∞Ïª§",
    items: [{ name: "ÌïôÍµê Í±¥Î¨º Ïä§Ìã∞Ïª§", price: 1000 }]
  },
  {
    name: "Îù†Î∂ÄÎù†Î∂ÄÏî∞",
    items: [
      { name: "1", price: 500 },
      { name: "2", price: 500 },
      { name: "3", price: 500 },
      { name: "4", price: 500 }
    ]
  },
  {
    name: "ÌïôÍµê Í±¥Î¨º Ìè¨Ïä§Ìä∏Ïûá",
    items: [
      { name: "1", price: 1000 },
      { name: "2", price: 1000 }
    ]
  }
];

export default function GoodsTracker() {
  const [goods, setGoods] = useState(() => {
    const saved = localStorage.getItem("hakHongGoods");
    return saved
      ? JSON.parse(saved)
      : categories.map((category) => ({
          name: category.name,
          items: category.items.map((item) => ({ ...item, sold: 0 }))
        }));
  });

  useEffect(() => {
    localStorage.setItem("hakHongGoods", JSON.stringify(goods));
  }, [goods]);

  const handleAdjust = (categoryIndex, itemIndex, delta) => {
    setGoods((prevGoods) =>
      prevGoods.map((category, cIndex) =>
        cIndex === categoryIndex
          ? {
              ...category,
              items: category.items.map((item, iIndex) =>
                iIndex === itemIndex
                  ? { ...item, sold: Math.max(0, item.sold + delta) }
                  : item
              )
            }
          : category
      )
    );
  };

  const totalRevenue = goods.reduce(
    (sum, category) =>
      sum +
      category.items.reduce((catSum, item) => catSum + item.price * item.sold, 0),
    0
  );

  const top3Items = goods
    .flatMap((category) =>
      category.items.map((item) => ({
        name: item.name,
        revenue: item.price * item.sold,
        sold: item.sold
      }))
    )
    .sort((a, b) => b.revenue - a.revenue)
    .slice(0, 3);

  return (
    <div className="flex flex-col items-center justify-start p-8 w-full">
      <h1 className="text-4xl font-bold text-pink-600 mb-8">
        üå∏ ÌïôÌôç ÍµøÏ¶à ÌåêÎß§ ÌòÑÌô© üå∏
      </h1>

      {/* ÏÉÅÌíà Î¶¨Ïä§Ìä∏Î•º Î∞òÎ∞ò Í∑∏Î¶¨ÎìúÎ°ú */}
      <div className="grid grid-cols-1 md:grid-cols-2 gap-6 w-full px-4">
        {goods.map((category, categoryIndex) => (
          <div
            key={categoryIndex}
            className="bg-white w-full p-6 rounded-3xl shadow-lg border border-gray-300"
          >
            <h2 className="text-2xl font-semibold mb-4 text-pink-600">
              {category.name}
            </h2>
            <table className="w-full border-collapse">
              <thead>
                <tr className="bg-pink-300 text-white text-lg">
                  <th className="p-2">ÏÉÅÌíàÎ™Ö</th>
                  <th className="p-2">Í∞ÄÍ≤©</th>
                  <th className="p-2">ÌåêÎß§ ÏàòÎüâ</th>
                  <th className="p-2">Ï°∞Ï†ï</th>
                </tr>
              </thead>
              <tbody>
                {category.items.map((item, itemIndex) => (
                  <tr key={itemIndex} className="border-b text-center text-base">
                    <td className="p-2">{item.name}</td>
                    <td className="p-2">{item.price}Ïõê</td>
                    <td className="p-2">{item.sold}</td>
                    <td className="p-2">
                      <div className="flex items-center justify-center space-x-2">
                        <button
                          onClick={() =>
                            handleAdjust(categoryIndex, itemIndex, -1)
                          }
                          className="bg-pink-500 text-white rounded-full w-8 h-8"
                        >
                          -
                        </button>
                        <button
                          onClick={() =>
                            handleAdjust(categoryIndex, itemIndex, 1)
                          }
                          className="bg-pink-500 text-white rounded-full w-8 h-8"
                        >
                          +
                        </button>
                      </div>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        ))}
      </div>

      {/* Ï¥ù ÌåêÎß§ Í∏àÏï° */}
      <div className="mt-12 text-3xl font-bold text-pink-700 flex flex-col items-center">
        <span>Ï¥ù ÌåêÎß§ Í∏àÏï°:</span>
        <span className="text-5xl text-rose-600 mt-2 drop-shadow-md">
          {totalRevenue.toLocaleString()}Ïõê
        </span>
      </div>

      {/* Top 3 ÍµøÏ¶à */}
      <div className="mt-10 w-full max-w-screen-md bg-yellow-100 border border-yellow-300 rounded-3xl p-6 shadow-md">
        <h2 className="text-2xl font-bold text-yellow-800 mb-4">
          üî• Top 3 Ïù∏Í∏∞ ÍµøÏ¶à üî•
        </h2>
        <ol className="list-decimal list-inside space-y-2 text-xl text-yellow-900">
          {top3Items.map((item, index) => (
            <li key={index}>
              {item.name} - {item.sold}Í∞ú ÌåêÎß§ ({item.revenue.toLocaleString()}Ïõê)
            </li>
          ))}
        </ol>
      </div>
    </div>
  );
}
