import { useState, useEffect } from "react";

// 상품 카테고리 및 해당 상품 목록
const categories = [
  { name: "교복 떡메", items: [
    { name: "남자 하복", price: 1500 },
    { name: "여자 하복", price: 1500 },
    { name: "1학년 생활복", price: 1500 },
    { name: "2학년 생활복", price: 1500 },
    { name: "3학년 생활복", price: 1500 }
  ]},
  { name: "학교 전경 엽서", items: [
    { name: "학교 전경 엽서", price: 1500 }
  ]},
  { name: "학교 건물 스티커", items: [
    { name: "학교 건물 스티커", price: 1000 }
  ]},
  { name: "띠부띠부씰", items: [
    { name: "1", price: 500 },
    { name: "2", price: 500 },
    { name: "3", price: 500 },
    { name: "4", price: 500 }
  ]},
  { name: "학교 건물 포스트잇", items: [
    { name: "1", price: 1000 },
    { name: "2", price: 1000 }
  ]}
];

export default function GoodsTracker() {
  // 상태 관리: 각 상품의 판매 수량을 추적
  const [goods, setGoods] = useState(() => {
    const saved = localStorage.getItem("hakHongGoods");
    return saved
      ? JSON.parse(saved)
      : categories.map((category) => ({
          name: category.name,
          items: category.items.map((item) => ({ ...item, sold: 0 })),
        }));
  });

  useEffect(() => {
    localStorage.setItem("hakHongGoods", JSON.stringify(goods));
  }, [goods]);

  // 판매 수량 증가 및 감소 함수
  const handleAdjust = (categoryIndex, itemIndex, delta) => {
    setGoods((prevGoods) =>
      prevGoods.map((category, cIndex) =>
        cIndex === categoryIndex
          ? {
              ...category,
              items: category.items.map((item, iIndex) =>
                iIndex === itemIndex ? { ...item, sold: Math.max(0, item.sold + delta) } : item
              )
            }
          : category
      )
    );
  };

  // 총 판매 금액 계산
  const totalRevenue = goods.reduce(
    (sum, category) =>
      sum + category.items.reduce((catSum, item) => catSum + item.price * item.sold, 0),
    0
  );

  return (
    <div className="bg-pink-100 min-h-screen flex flex-col items-center justify-center p-6">
      <h1 className="text-4xl font-bold text-pink-600 mb-6">🌸 학홍 굿즈 판매 현황 🌸</h1>

      <div className="w-full max-w-5xl">
        {goods.map((category, categoryIndex) => (
          <div key={categoryIndex} className="bg-gray-100 p-6 rounded-3xl shadow-lg mb-6 border border-gray-300">
            <h2 className="text-2xl font-bold text-gray-700 mb-4">{category.name}</h2>
            <div className="bg-white p-4 rounded-xl shadow">
              <table className="w-full border-collapse">
                <thead className="bg-pink-300 text-white">
                  <tr>
                    <th className="p-3">상품명</th>
                    <th className="p-3">가격</th>
                    <th className="p-3">판매 수량</th>
                    <th className="p-3">조정</th>
                    <th className="p-3">상품명</th>
                    <th className="p-3">가격</th>
                    <th className="p-3">판매 수량</th>
                    <th className="p-3">조정</th>
                  </tr>
                </thead>
                <tbody>
                  {Array.from({ length: Math.ceil(category.items.length / 2) }).map((_, rowIdx) => {
                    const leftItem = category.items[rowIdx * 2];
                    const rightItem = category.items[rowIdx * 2 + 1];

                    return (
                      <tr key={rowIdx} className="border-b text-center text-lg">
                        {/* 왼쪽 아이템 */}
                        {leftItem ? (
                          <>
                            <td className="p-3">{leftItem.name}</td>
                            <td className="p-3">{leftItem.price}원</td>
                            <td className="p-3">{leftItem.sold}</td>
                            <td className="p-3">
                              <button
                                onClick={() => handleAdjust(categoryIndex, rowIdx * 2, -1)}
                                className="bg-pink-500 text-white px-3 py-1 rounded-full mx-1"
                              >
                                -
                              </button>
                              <button
                                onClick={() => handleAdjust(categoryIndex, rowIdx * 2, 1)}
                                className="bg-pink-500 text-white px-3 py-1 rounded-full mx-1"
                              >
                                +
                              </button>
                            </td>
                          </>
                        ) : <td colSpan={4}></td>}

                        {/* 오른쪽 아이템 */}
                        {rightItem ? (
                          <>
                            <td className="p-3">{rightItem.name}</td>
                            <td className="p-3">{rightItem.price}원</td>
                            <td className="p-3">{rightItem.sold}</td>
                            <td className="p-3">
                              <button
                                onClick={() => handleAdjust(categoryIndex, rowIdx * 2 + 1, -1)}
                                className="bg-pink-500 text-white px-3 py-1 rounded-full mx-1"
                              >
                                -
                              </button>
                              <button
                                onClick={() => handleAdjust(categoryIndex, rowIdx * 2 + 1, 1)}
                                className="bg-pink-500 text-white px-3 py-1 rounded-full mx-1"
                              >
                                +
                              </button>
                            </td>
                          </>
                        ) : <td colSpan={4}></td>}
                      </tr>
                    );
                  })}
                </tbody>
              </table>
            </div>
          </div>
        ))}
      </div>

      <div className="mt-6 text-3xl font-bold text-pink-700">
        총 판매 금액: {totalRevenue.toLocaleString()}원
      </div>
    </div>
  );
}
