import { useState } from "react";

const categories = [
  { name: "ÍµêÎ≥µ Îñ°Î©î", items: [
    { name: "ÎÇ®Ïûê ÌïòÎ≥µ", price: 1500 },
    { name: "Ïó¨Ïûê ÌïòÎ≥µ", price: 1500 },
    { name: "1ÌïôÎÖÑ ÏÉùÌôúÎ≥µ", price: 1500 },
    { name: "2ÌïôÎÖÑ ÏÉùÌôúÎ≥µ", price: 1500 },
    { name: "3ÌïôÎÖÑ ÏÉùÌôúÎ≥µ", price: 1500 }
  ]},
  { name: "ÌïôÍµê Ï†ÑÍ≤Ω ÏóΩÏÑú", items: [
    { name: "ÌïôÍµê Ï†ÑÍ≤Ω ÏóΩÏÑú", price: 1500 }
  ]},
  { name: "ÌïôÍµê Í±¥Î¨º Ïä§Ìã∞Ïª§", items: [
    { name: "ÌïôÍµê Í±¥Î¨º Ïä§Ìã∞Ïª§", price: 1000 }
  ]},
  { name: "Îù†Î∂ÄÎù†Î∂ÄÏî∞", items: [
    { name: "Îù†Î∂ÄÎù†Î∂ÄÏî∞ 1", price: 500 },
    { name: "Îù†Î∂ÄÎù†Î∂ÄÏî∞ 2", price: 500 },
    { name: "Îù†Î∂ÄÎù†Î∂ÄÏî∞ 3", price: 500 },
    { name: "Îù†Î∂ÄÎù†Î∂ÄÏî∞ 4", price: 500 }
  ]},
  { name: "ÌïôÍµê Í±¥Î¨º Ìè¨Ïä§Ìä∏Ïûá", items: [
    { name: "ÌïôÍµê Í±¥Î¨º Ìè¨Ïä§Ìä∏Ïûá 1", price: 1000 },
    { name: "ÌïôÍµê Í±¥Î¨º Ìè¨Ïä§Ìä∏Ïûá 2", price: 1000 }
  ]}
];

export default function GoodsTracker() {
  const [goods, setGoods] = useState(
    categories.map(category => ({
      name: category.name,
      items: category.items.map(item => ({ ...item, sold: 0 }))
    }))
  );

  const handleAdjust = (categoryIndex, itemIndex, delta) => {
    setGoods(prevGoods =>
      prevGoods.map((category, cIndex) =>
        cIndex === categoryIndex
          ? {
              ...category,
              items: category.items.map((item, iIndex) =>
                iIndex === itemIndex ? { ...item, sold: Math.max(0, item.sold + delta) } : item
              )
            }
          : category
      )
    );
  };

  const totalRevenue = goods.reduce(
    (sum, category) =>
      sum + category.items.reduce((catSum, item) => catSum + item.price * item.sold, 0),
    0
  );

  return (
    <div className="bg-pink-100 min-h-screen flex flex-col items-center justify-center p-6">
      <h1 className="text-4xl font-bold text-pink-600 mb-6">üå∏ ÌïôÌôç ÍµøÏ¶à ÌåêÎß§ ÌòÑÌô© üå∏</h1>
      <div className="w-full max-w-3xl grid grid-cols-1 gap-6">
        {goods.map((category, categoryIndex) => (
          <div key={categoryIndex} className="bg-white p-6 rounded-2xl shadow-lg mb-6">
            <div className="grid grid-cols-1 gap-4">
              {category.items.map((item, itemIndex) => (
                <div key={itemIndex} className="flex justify-between items-center">
                  <div className="text-lg font-bold text-pink-700">{item.name}</div>
                  <div className="text-lg">{item.price}Ïõê</div>
                  <div className="text-lg">{item.sold}</div>
                  <div>
                    <button
                      onClick={() => handleAdjust(categoryIndex, itemIndex, -1)}
                      className="bg-pink-500 text-white px-4 py-2 rounded-full mx-2"
                    >
                      -
                    </button>
                    <button
                      onClick={() => handleAdjust(categoryIndex, itemIndex, 1)}
                      className="bg-pink-500 text-white px-4 py-2 rounded-full mx-2"
                    >
                      +
                    </button>
                  </div>
                </div>
              ))}
            </div>
          </div>
        ))}
      </div>
      <div className="mt-6 text-3xl font-bold text-pink-700">
        Ï¥ù ÌåêÎß§ Í∏àÏï°: {totalRevenue.toLocaleString()}Ïõê
      </div>
    </div>
  );
}
