'use client';

import { useState } from 'react';

export default function Page() {
  const [goods, setGoods] = useState([
    {
      category: 'ÏùòÎ•ò',
      items: [
        { name: 'ÎÇ®Ïûê ÌïòÎ≥µ', price: 30000, sold: 0 },
        { name: 'Ïó¨Ïûê ÌïòÎ≥µ', price: 32000, sold: 0 },
        { name: 'ÎÇ®Ïûê ÎèôÎ≥µ', price: 35000, sold: 0 },
        { name: 'Ïó¨Ïûê ÎèôÎ≥µ', price: 37000, sold: 0 },
      ],
    },
    {
      category: 'Î¨∏Íµ¨Î•ò',
      items: [
        { name: 'Î°úÍ≥† Ìéú', price: 2000, sold: 0 },
        { name: 'ÎÖ∏Ìä∏ ÏÑ∏Ìä∏', price: 5000, sold: 0 },
      ],
    },
    {
      category: 'Í∏∞ÎÖêÌíà',
      items: [
        { name: 'ÌïôÍµê Í±¥Î¨º Ïä§Ìã∞Ïª§', price: 1500, sold: 0 },
        { name: 'ÏóΩÏÑú ÏÑ∏Ìä∏', price: 3000, sold: 0 },
      ],
    },
  ]);

  const handleSell = (categoryIndex: number, itemIndex: number) => {
    const newGoods = [...goods];
    newGoods[categoryIndex].items[itemIndex].sold++;
    setGoods(newGoods);
  };

  const totalRevenue = goods.reduce(
    (sum, category) =>
      sum + category.items.reduce((catSum, item) => catSum + item.price * item.sold, 0),
    0
  );

  return (
    <div className="p-8 max-w-4xl mx-auto">
      <h1 className="text-3xl font-bold text-center mb-8">üéÅ ÌïôÏÉù ÍµøÏ¶à ÌåêÎß§ ÌòÑÌô©</h1>

      {goods.map((category, catIdx) => (
        <div key={catIdx} className="mb-8">
          <h2 className="text-2xl font-semibold mb-4">üì¶ {category.category}</h2>
          <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
            {category.items.map((item, itemIdx) => (
              <div
                key={itemIdx}
                className="border p-4 rounded-lg shadow-md flex justify-between items-center"
              >
                <div>
                  <h3 className="text-lg font-bold">{item.name}</h3>
                  <p className="text-gray-600">Í∞ÄÍ≤©: {item.price.toLocaleString()}Ïõê</p>
                  <p className="text-gray-600">ÌåêÎß§ ÏàòÎüâ: {item.sold}Í∞ú</p>
                </div>
                <button
                  onClick={() => handleSell(catIdx, itemIdx)}
                  className="bg-pink-500 hover:bg-pink-600 text-white px-4 py-2 rounded"
                >
                  ÌåêÎß§
                </button>
              </div>
            ))}
          </div>
        </div>
      ))}

      <div className="mt-8 text-xl font-bold text-center">
        üí∞ Ï¥ù ÌåêÎß§ Í∏àÏï°: {totalRevenue.toLocaleString()}Ïõê
      </div>

      {/* Top 3 ÍµøÏ¶à Î∂ÑÏÑù */}
      <div className="mt-12 w-full max-w-screen-md mx-auto">
        <h2 className="text-2xl font-bold text-pink-700 mb-4">üî• ÌåêÎß§ ÏÉÅÏúÑ TOP 3 ÍµøÏ¶à</h2>

        {/* Îç∞Ïù¥ÌÑ∞ Ï∂îÏ∂ú Î∞è ÌëúÏãú */}
        {(() => {
          // Î™®Îì† ÏïÑÏù¥ÌÖú ÌèâÌÉÑÌôî
          const allItems = goods.flatMap((category) =>
            category.items.map((item) => ({
              name: item.name,
              sold: item.sold,
            }))
          );

          // ÌåêÎß§Îüâ Í∏∞Ï§Ä ÎÇ¥Î¶ºÏ∞®Ïàú Ï†ïÎ†¨
          const sortedItems = allItems
            .filter((item) => item.sold > 0)
            .sort((a, b) => b.sold - a.sold)
            .slice(0, 3);

          const maxSold = sortedItems[0]?.sold || 1;

          return sortedItems.length === 0 ? (
            <p className="text-gray-500">ÏïÑÏßÅ ÌåêÎß§Îêú ÍµøÏ¶àÍ∞Ä ÏóÜÏäµÎãàÎã§.</p>
          ) : (
            <div className="space-y-4">
              {sortedItems.map((item, index) => (
                <div key={index}>
                  <div className="flex justify-between mb-1">
                    <span className="font-medium">
                      {index + 1}. {item.name}
                    </span>
                    <span className="text-pink-600 font-semibold">{item.sold}Í∞ú</span>
                  </div>
                  <div className="w-full bg-pink-100 rounded-full h-5">
                    <div
                      className="bg-pink-500 h-5 rounded-full"
                      style={{ width: `${(item.sold / maxSold) * 100}%` }}
                    ></div>
                  </div>
                </div>
              ))}
            </div>
          );
        })()}
      </div>
    </div>
  );
}
