import { useState, useEffect } from "react";

const categories = [
  {
    name: "ÍµêÎ≥µ Îñ°Î©î", items: [
      { name: "ÎÇ®Ïûê ÌïòÎ≥µ", price: 1500 },
      { name: "Ïó¨Ïûê ÌïòÎ≥µ", price: 1500 },
      { name: "1ÌïôÎÖÑ ÏÉùÌôúÎ≥µ", price: 1500 },
      { name: "2ÌïôÎÖÑ ÏÉùÌôúÎ≥µ", price: 1500 },
      { name: "3ÌïôÎÖÑ ÏÉùÌôúÎ≥µ", price: 1500 }
    ]
  },
  {
    name: "ÌïôÍµê Ï†ÑÍ≤Ω ÏóΩÏÑú", items: [
      { name: "ÌïôÍµê Ï†ÑÍ≤Ω ÏóΩÏÑú", price: 1500 }
    ]
  },
  {
    name: "ÌïôÍµê Í±¥Î¨º Ïä§Ìã∞Ïª§", items: [
      { name: "ÌïôÍµê Í±¥Î¨º Ïä§Ìã∞Ïª§", price: 1000 }
    ]
  },
  {
    name: "Îù†Î∂ÄÎù†Î∂ÄÏî∞", items: [
      { name: "1", price: 500 },
      { name: "2", price: 500 },
      { name: "3", price: 500 },
      { name: "4", price: 500 }
    ]
  },
  {
    name: "ÌïôÍµê Í±¥Î¨º Ìè¨Ïä§Ìä∏Ïûá", items: [
      { name: "1", price: 1000 },
      { name: "2", price: 1000 }
    ]
  }
];

export default function GoodsTracker() {
  const [goods, setGoods] = useState(() => {
    const saved = localStorage.getItem("hakHongGoods");
    return saved
      ? JSON.parse(saved)
      : categories.map((category) => ({
          name: category.name,
          items: category.items.map((item) => ({ ...item, sold: 0 })),
        }));
  });

  useEffect(() => {
    localStorage.setItem("hakHongGoods", JSON.stringify(goods));
  }, [goods]);

  const handleAdjust = (categoryIndex, itemIndex, delta) => {
    setGoods((prevGoods) =>
      prevGoods.map((category, cIndex) =>
        cIndex === categoryIndex
          ? {
              ...category,
              items: category.items.map((item, iIndex) =>
                iIndex === itemIndex
                  ? { ...item, sold: Math.max(0, item.sold + delta) }
                  : item
              )
            }
          : category
      )
    );
  };

  const totalRevenue = goods.reduce(
    (sum, category) =>
      sum + category.items.reduce((catSum, item) => catSum + item.price * item.sold, 0),
    0
  );

  const leftCategories = goods.slice(0, 3);
  const rightCategories = goods.slice(3);

  return (
    <div className="bg-pink-100 w-screen h-screen flex flex-col items-center justify-start p-8 overflow-hidden">
      <h1 className="text-4xl font-bold text-pink-600 mb-8">
        üå∏ ÌïôÌôç ÍµøÏ¶à ÌåêÎß§ ÌòÑÌô© üå∏
      </h1>

      {/* Ï¢åÏö∞ ÎÇòÎàÑÍ∏∞ */}
      <div className="flex justify-between w-full max-w-screen-xl gap-8">
        {/* ÏôºÏ™Ω */}
        <div className="flex flex-col space-y-6 w-1/2">
          {leftCategories.map((category, categoryIndex) => (
            <div
              key={categoryIndex}
              className="bg-white w-full p-6 rounded-3xl shadow-lg border border-gray-300"
            >
              <h2 className="text-2xl font-semibold mb-4 text-pink-600">
                {category.name}
              </h2>
              <table className="w-full border-collapse">
                <thead>
                  <tr className="bg-pink-300 text-white text-lg">
                    <th className="p-4">ÏÉÅÌíàÎ™Ö</th>
                    <th className="p-4">Í∞ÄÍ≤©</th>
                    <th className="p-4">ÌåêÎß§ ÏàòÎüâ</th>
                    <th className="p-4">Ï°∞Ï†ï</th>
                  </tr>
                </thead>
                <tbody>
                  {category.items.map((item, itemIndex) => (
                    <tr
                      key={itemIndex}
                      className="border-b text-center text-lg"
                    >
                      <td className="p-4">{item.name}</td>
                      <td className="p-4">{item.price}Ïõê</td>
                      <td className="p-4">{item.sold}</td>
                      <td className="p-4">
                        <button
                          onClick={() =>
                            handleAdjust(categoryIndex, itemIndex, -1)
                          }
                          className="bg-pink-500 text-white px-4 py-2 rounded-full mx-2"
                        >
                          -
                        </button>
                        <button
                          onClick={() =>
                            handleAdjust(categoryIndex, itemIndex, 1)
                          }
                          className="bg-pink-500 text-white px-4 py-2 rounded-full mx-2"
                        >
                          +
                        </button>
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          ))}
        </div>

        {/* Ïò§Î•∏Ï™Ω */}
        <div className="flex flex-col space-y-6 w-1/2">
          {rightCategories.map((category, i) => {
            const categoryIndex = i + 3;
            return (
              <div
                key={categoryIndex}
                className="bg-white w-full p-6 rounded-3xl shadow-lg border border-gray-300"
              >
                <h2 className="text-2xl font-semibold mb-4 text-pink-600">
                  {category.name}
                </h2>
                <table className="w-full border-collapse">
                  <thead>
                    <tr className="bg-pink-300 text-white text-lg">
                      <th className="p-4">ÏÉÅÌíàÎ™Ö</th>
                      <th className="p-4">Í∞ÄÍ≤©</th>
                      <th className="p-4">ÌåêÎß§ ÏàòÎüâ</th>
                      <th className="p-4">Ï°∞Ï†ï</th>
                    </tr>
                  </thead>
                  <tbody>
                    {category.items.map((item, itemIndex) => (
                      <tr
                        key={itemIndex}
                        className="border-b text-center text-lg"
                      >
                        <td className="p-4">{item.name}</td>
                        <td className="p-4">{item.price}Ïõê</td>
                        <td className="p-4">{item.sold}</td>
                        <td className="p-4">
                          <button
                            onClick={() =>
                              handleAdjust(categoryIndex, itemIndex, -1)
                            }
                            className="bg-pink-500 text-white px-4 py-2 rounded-full mx-2"
                          >
                            -
                          </button>
                          <button
                            onClick={() =>
                              handleAdjust(categoryIndex, itemIndex, 1)
                            }
                            className="bg-pink-500 text-white px-4 py-2 rounded-full mx-2"
                          >
                            +
                          </button>
                        </td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              </div>
            );
          })}
        </div>
      </div>

      {/* Ï¥ù ÌåêÎß§ Í∏àÏï° */}
      <div className="mt-10 text-3xl font-bold text-pink-700">
        Ï¥ù ÌåêÎß§ Í∏àÏï°: {totalRevenue.toLocaleString()}Ïõê
      </div>
    </div>
  );
}
