import { useState, useEffect } from "react";

const leftCategories = [
  {
    name: "ÍµêÎ≥µ Îñ°Î©î",
    items: [
      { name: "ÎÇ®Ïûê ÌïòÎ≥µ", price: 1500 },
      { name: "Ïó¨Ïûê ÌïòÎ≥µ", price: 1500 },
      { name: "1ÌïôÎÖÑ ÏÉùÌôúÎ≥µ", price: 1500 },
      { name: "2ÌïôÎÖÑ ÏÉùÌôúÎ≥µ", price: 1500 },
      { name: "3ÌïôÎÖÑ ÏÉùÌôúÎ≥µ", price: 1500 },
    ],
  },
  {
    name: "ÌïôÍµê Ï†ÑÍ≤Ω ÏóΩÏÑú",
    items: [{ name: "ÌïôÍµê Ï†ÑÍ≤Ω ÏóΩÏÑú", price: 1500 }],
  },
];

const rightCategories = [
  {
    name: "ÌïôÍµê Í±¥Î¨º Ïä§Ìã∞Ïª§",
    items: [{ name: "ÌïôÍµê Í±¥Î¨º Ïä§Ìã∞Ïª§", price: 1000 }],
  },
  {
    name: "Îù†Î∂ÄÎù†Î∂ÄÏî∞",
    items: [
      { name: "1", price: 500 },
      { name: "2", price: 500 },
      { name: "3", price: 500 },
      { name: "4", price: 500 },
    ],
  },
  {
    name: "ÌïôÍµê Í±¥Î¨º Ìè¨Ïä§Ìä∏Ïûá",
    items: [
      { name: "1", price: 1000 },
      { name: "2", price: 1000 },
    ],
  },
];

export default function GoodsTracker() {
  const [goods, setGoods] = useState(() => {
    const saved = localStorage.getItem("hakHongGoods");
    return saved
      ? JSON.parse(saved)
      : [...leftCategories, ...rightCategories].map((category) => ({
          name: category.name,
          items: category.items.map((item) => ({ ...item, sold: 0 })),
        }));
  });

  useEffect(() => {
    localStorage.setItem("hakHongGoods", JSON.stringify(goods));
  }, [goods]);

  const handleAdjust = (categoryIndex, itemIndex, delta) => {
    setGoods((prevGoods) =>
      prevGoods.map((category, cIndex) =>
        cIndex === categoryIndex
          ? {
              ...category,
              items: category.items.map((item, iIndex) =>
                iIndex === itemIndex
                  ? { ...item, sold: Math.max(0, item.sold + delta) }
                  : item
              ),
            }
          : category
      )
    );
  };

  const totalRevenue = goods.reduce(
    (sum, category) =>
      sum + category.items.reduce((catSum, item) => catSum + item.price * item.sold, 0),
    0
  );

  const renderCategory = (category, categoryIndex) => (
    <div key={categoryIndex} className="bg-gray-100 p-6 rounded-3xl shadow-lg mb-6 border border-gray-300 w-full">
      <div className="bg-white p-4 rounded-xl shadow">
        <table className="w-full border-collapse">
          <thead>
            <tr className="bg-pink-300 text-white text-lg">
              <th className="p-4">ÏÉÅÌíàÎ™Ö</th>
              <th className="p-4">Í∞ÄÍ≤©</th>
              <th className="p-4">ÌåêÎß§ ÏàòÎüâ</th>
              <th className="p-4">Ï°∞Ï†ï</th>
            </tr>
          </thead>
          <tbody>
            {category.items.map((item, itemIndex) => (
              <tr key={itemIndex} className="border-b text-center text-lg">
                <td className="p-4">{item.name}</td>
                <td className="p-4">{item.price}Ïõê</td>
                <td className="p-4">{item.sold}</td>
                <td className="p-4">
                  <button
                    onClick={() => handleAdjust(categoryIndex, itemIndex, -1)}
                    className="bg-pink-500 text-white px-6 py-3 rounded-full mx-3"
                  >
                    -
                  </button>
                  <button
                    onClick={() => handleAdjust(categoryIndex, itemIndex, 1)}
                    className="bg-pink-500 text-white px-6 py-3 rounded-full mx-3"
                  >
                    +
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );

  return (
    <div className="bg-pink-100 min-h-screen flex flex-col items-center justify-center p-6">
      <h1 className="text-4xl font-bold text-pink-600 mb-6">üå∏ ÌïôÌôç ÍµøÏ¶à ÌåêÎß§ ÌòÑÌô© üå∏</h1>

      <div className="flex flex-col lg:flex-row gap-8 w-full max-w-7xl">
        <div className="flex-1">
          {goods.slice(0, leftCategories.length).map((cat, index) => renderCategory(cat, index))}
        </div>
        <div className="flex-1">
          {goods.slice(leftCategories.length).map((cat, index) =>
            renderCategory(cat, index + leftCategories.length)
          )}
        </div>
      </div>

      <div className="mt-6 text-3xl font-bold text-pink-700">
        Ï¥ù ÌåêÎß§ Í∏àÏï°: {totalRevenue.toLocaleString()}Ïõê
      </div>
    </div>
  );
}
