import { useState } from "react";

const goods = [
  { id: 1, name: "교복 떡메", types: ["남자 하복", "여자 하복", "1학년 생활복", "2학년 생활복", "3학년 생활복"], price: 1500 },
  { id: 2, name: "학교 전경 엽서", types: ["기본"], price: 1500 },
  { id: 3, name: "학교 건물 스티커", types: ["기본"], price: 1000 },
  { id: 4, name: "띠부띠부씰", types: ["1", "2", "3", "4"], price: 500 },
  { id: 5, name: "학교 건물 포스트잇", types: ["1", "2"], price: 1000 }
];

export default function GoodsTracker() {
  const [sales, setSales] = useState(
    goods.reduce((acc, item) => {
      item.types.forEach(type => {
        acc[`${item.id}-${type}`] = { count: 0, total: 0 };
      });
      return acc;
    }, {})
  );

  const updateSales = (key, price, delta) => {
    setSales(prev => {
      const newCount = Math.max(prev[key].count + delta, 0);
      return {
        ...prev,
        [key]: { count: newCount, total: newCount * price }
      };
    });
  };

  const totalSales = Object.values(sales).reduce((sum, item) => sum + item.total, 0);

  return (
    <div className="p-4">
      <h1 className="text-xl font-bold mb-4">굿즈 판매 현황</h1>
      <div className="grid gap-4">
        {goods.map(({ id, name, types, price }) => (
          <div key={id} className="border p-2 rounded">
            <h2 className="font-semibold">{name} ({price}원)</h2>
            {types.map(type => {
              const key = `${id}-${type}`;
              return (
                <div key={key} className="flex justify-between items-center my-2">
                  <span>{type}</span>
                  <div className="flex gap-2">
                    <button onClick={() => updateSales(key, price, -1)} className="px-2 bg-red-500 text-white">-</button>
                    <span>{sales[key].count}개</span>
                    <button onClick={() => updateSales(key, price, 1)} className="px-2 bg-green-500 text-white">+</button>
                  </div>
                </div>
              );
            })}
          </div>
        ))}
      </div>
      <h2 className="text-lg font-bold mt-4">총 판매 금액: {totalSales.toLocaleString()}원</h2>
    </div>
  );
}
